import React from 'react';
// import PropTypes from 'prop-types';
// import { bindActionCreators } from 'redux'
// import { connect } from 'react-redux';
// import { withRouter } from 'react-router-dom';
// import { Row } from 'react-bootstrap';
import './App.css';
import {
  interaction, layer, custom, control, //name spaces
  Interactions, Overlays, Controls,     //group
  Map, Layers, Overlay, Util    //objects
} from "react-openlayers";

export class App extends React.Component {
  // showPopup = (evt) => {
  //   this.overlayComp.overlay.setPosition(evt.coordinate);
  //   var lonlat = ol.proj.transform(evt.coordinate, 'EPSG:3857', 'EPSG:4326');

  //   this.popupComp.setContents(
  //     `<p>You clicked here:</p><code> ${lonlat[0]}, ${lonlat[1]}</code>`
  //   );
  //   this.popupComp.show();
  // }
  constructor(props, context) {
    super(props, context);

    this.state = {
    };
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <div className="App-header-back">
            <p className="App-header-title">Felicidad Mundial</p>
          </div>
          <div className="App-map">
          <Map view={{center: [0, 0], zoom: 2}} onClick={showPopup}>
      <Layers>
        <layer.Tile/>
        <layer.Vector source={markers} style={markers.style} zIndex="1" />
      </Layers>
      <Overlays>
        <Overlay 
          ref={comp => this.overlayComp = comp}
          element="#popup" />
      </Overlays>
      <Controls attribution={false} zoom={true}>
        <control.Rotate />
        <control.ScaleLine />
        <control.FullScreen />
        <control.OverviewMap />
        <control.ZoomSlider />
        <control.ZoomToExtent />
        <control.Zoom />
      </Controls>
      <Interactions>
        <interaction.Select style={selectedMarkerStyle} />
        <interaction.Draw source={markers} type='Point' />
        <interaction.Modify features={markers.features} />
      </Interactions>
    </Map>

    <custom.Popup ref={comp => this.popupComp = comp}>
    </custom.Popup>
          </div>
        </header>
      </div>
    );
    }
}

export default App;
