/* App.js */
import React from 'react';
import CanvasJSReact from './canvasjs.react';
import info2015 from './Data/2015.json';
import info2016 from './Data/2016.json';
import info2017 from './Data/2017.json';
import info2018 from './Data/2018.json';
import info2019 from './Data/2019.json';

var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

class Data extends React.Component {
    constructor(props, context) {
        super(props, context);    
        this.state={
            i2015: {},
            i2016: {},
            i2017: {},
            i2018: {},
            i2019: {}
        }
    }

    getData(country){
        info2015.forEach(info => {
            if (info.Country === country) {
                this.setState({i2015:{happiness: info['Happiness Score'], heald: info.Health}})
            }
        });
        info2016.forEach(info => {
            if (info.Country === country) {
                this.setState({i2016:{happiness: info['Happiness Score'], heald: info.Health}})
            }
        });
        info2017.forEach(info => {
            if (info.Country === country) {
                this.setState({i2017:{happiness: info['Happiness Score'], heald: info.Health}})
            }
        });
        info2018.forEach(info => {
            if (info.Country === country) {
                this.setState({i2018:{happiness: info['Happiness Score'], heald: info.Health}})
            }
        });
        info2019.forEach(info => {
            if (info.Country === country) {
                this.setState({i2019:{happiness: info['Happiness Score'], heald: info.Health}})
            }
        });
    }	
	render() {
		const options = {
			animationEnabled: true,
			theme: "light2",
			title:{
				text: "Promedio de felicidad durante los últimos 5 años"
			},
			axisX:{
				valueFormatString: "YYYY",
				crosshair: {
					enabled: true,
					snapToDataPoint: true
				}
			},
			axisY: {
				title: "Valor",
				includeZero: false,
				valueFormatString: "#0",
				crosshair: {
					enabled: true,
					snapToDataPoint: true,
					labelFormatter: function(e) {
						return CanvasJS.formatNumber(e.value, "#0");
					}
				}
			},
			data: [{
				type: "area",
				xValueFormatString: "DD MMM",
				yValueFormatString: "€##0.00",
				dataPoints: [
				  { x: new Date("2018-03-01"), y: 85.3},
				  { x: new Date("2018-03-02"), y: 83.97},
				  { x: new Date("2018-03-05"), y: 83.49},
				  { x: new Date("2018-03-06"), y: 84.16},
				  { x: new Date("2018-03-07"), y: 84.86},
				  { x: new Date("2018-03-08"), y: 84.97},
				  { x: new Date("2018-03-09"), y: 85.13},
				  { x: new Date("2018-03-12"), y: 85.71},
				  { x: new Date("2018-03-13"), y: 84.63},
				  { x: new Date("2018-03-14"), y: 84.17},
				  { x: new Date("2018-03-15"), y: 85.12},
				  { x: new Date("2018-03-16"), y: 85.86},
				  { x: new Date("2018-03-19"), y: 85.17},
				  { x: new Date("2018-03-20"), y: 85.99},
				  { x: new Date("2018-03-21"), y: 86.1},
				  { x: new Date("2018-03-22"), y: 85.33},
				  { x: new Date("2018-03-23"), y: 84.18},
				  { x: new Date("2018-03-26"), y: 85.21},
				  { x: new Date("2018-03-27"), y: 85.81},
				  { x: new Date("2018-03-28"), y: 85.56},
				  { x: new Date("2018-03-29"), y: 88.15}
				]
			}]
		}

		return (
		<div>
			<CanvasJSChart options = {options} 
				/* onRef={ref => this.chart = ref} */
			/>
			{/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
		</div>
		);
	}
}

export default Data;
