import React, { useEffect, useRef } from 'react';
// import PropTypes from 'prop-types';
// import { bindActionCreators } from 'redux'
// import { connect } from 'react-redux';
// import { withRouter } from 'react-router-dom';
// import { Row } from 'react-bootstrap';
import './App.css';
import ArcGISMap from 'esri/Map';
import MapView from 'esri/views/MapView';
// import {
//   interaction, layer, custom, control, //name spaces
//   Interactions, Overlays, Controls,     //group
//   Map, Layers, Overlay, Util    //objects
// } from "react-openlayers";

export class App extends React.Component {
  // showPopup = (evt) => {
  //   this.overlayComp.overlay.setPosition(evt.coordinate);
  //   var lonlat = ol.proj.transform(evt.coordinate, 'EPSG:3857', 'EPSG:4326');

  //   this.popupComp.setContents(
  //     `<p>You clicked here:</p><code> ${lonlat[0]}, ${lonlat[1]}</code>`
  //   );
  //   this.popupComp.show();
  // }
  constructor(props, context) {
    super(props, context);

    this.state = {
    };
    this.mapRef = React.createRef();
  }

  componentDidMount() {
    // create map
    const map = new ArcGISMap({
      basemap: 'topo-vector'
    });
    // load the map view at the ref's DOM node
    this.view = new MapView({
      container: this.mapRef.current,
      map: map,
      center: [-118, 34],
      zoom: 8
    });
  }

  componentWillUnmount() {
    if (this.view) {
      // destroy the map view
      this.view.container = null;
    }
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <div className="App-header-back">
            <p className="App-header-title">Felicidad Mundial</p>
          </div>
          <div className="App-map">
          <div className="webmap" ref={this.mapRef} />
          </div>
        </header>
      </div>
    );
    }
}

export default App;
